import axios from "axios";
import { useState } from "react";
import "./style.css";

const Compress = () => {
  const [fileInputs, setFileInputs] = useState([
    { id: Date.now(), file: null },
    { id: Date.now() + 1, file: null },
    { id: Date.now() + 2, file: null },
    { id: Date.now() + 3, file: null },
  ]);
  const [downloadLinks, setDownloadLinks] = useState([]);
  const [compressionRatios, setCompressionRatios] = useState([]);
  const [uploading, setUploading] = useState(false);

  const handleFileChange = (id, e) => {
    setFileInputs((prev) =>
      prev.map((input) =>
        input.id === id ? { ...input, file: e.target.files[0] } : input
      )
    );
  };

  const handleUpload = async () => {
    const filesToUpload = fileInputs
      .map((input) => input.file)
      .filter((file) => file);

    if (filesToUpload.length === 0) {
      alert("Please select at least one file first.");
      return;
    }

    const formData = new FormData();
    filesToUpload.forEach((file) => {
      formData.append("audio", file);
    });

    setUploading(true);
    try {
      const response = await axios.post(
        "http://localhost:5000/compress",
        formData,
        {
          headers: {
            "Content-Type": "multipart/form-data",
          },
        }
      );
      console.log("API Response:", response.data);

      const { compressedFiles, compressionRatios } = response.data;

      const urls = compressedFiles.map(
        (file) => `http://localhost:5000/${file}`
      );

      setDownloadLinks(urls);
      setCompressionRatios(compressionRatios);
      setUploading(false);
    } catch (error) {
      console.error("Error uploading files:", error);
      setUploading(false);
    }
  };

  const handleDownload = (index) => {
    const downloadLink = downloadLinks[index];
    if (downloadLink) {
      const link = document.createElement("a");
      link.href = downloadLink;
      link.download = "";
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
    } else {
      alert("No file available to download.");
    }
  };

  return (
    <div className="wrap">
      <div className="compresComp">
        <div className="title1">Convert</div>
        <div className="title2">Choose files to convert to MPEG-4 audio</div>
        <div className="fileToCompress">
          {fileInputs.map((input) => (
            <div key={input.id} className="custom-file-upload">
              <input
                type="file"
                accept="audio/*"
                onChange={(e) => handleFileChange(input.id, e)}
              />
            </div>
          ))}
          <div className="uploadBtn">
            <button onClick={handleUpload}>
              {uploading ? "Uploading..." : "Convert"}
            </button>
          </div>
          <div className="compRat">
            {compressionRatios.length > 0 &&
              compressionRatios.map((ratio, index) => (
                <div className="ratio" key={index}>
                  <p>
                    Compression Ratio for file {index + 1}: {ratio}%
                  </p>
                </div>
              ))}
          </div>
          {downloadLinks.length > 0 &&
            downloadLinks.map((link, index) => (
              <div className="downloadBtn" key={index}>
                <button onClick={() => handleDownload(index)}>
                  Download Converted Audio {index + 1}
                </button>
              </div>
            ))}
        </div>
      </div>
    </div>
  );
};

export default Compress;
