import axios from "axios";
import { useState } from "react";
import "./style.css";
import { Progress } from "antd";

const FileSelectComponent = ({ addRecord }) => {
  const [file, setFile] = useState(null);
  const [downloadLink, setDownloadLink] = useState("");
  const [compressionRatio, setCompressionRatio] = useState("");
  const [uploading, setUploading] = useState(false);
  const [uploadProgress, setUploadProgress] = useState(0);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };
 const options = {
   onUploadProgress: (progressEvent) => {
     const { loaded, total } = progressEvent;

     let percent = Math.floor((loaded * 100) / total);
     console.log(`${loaded}kb of ${total}kb | ${percent}%`);

     if (percent < 100) {
       setUploadPercentage(percent);
     }
   },
   cancelToken: new CancelToken(
     (cancel) => (cancelFileUpload.current = cancel)
   ),
 };
  const handleUpload = async () => {
    if (!file) {
      alert("Please select a file first.");
      return;
    }

    const formData = new FormData();
    formData.append("audio", file);

    setUploading(true);
    setUploadProgress(0);

    try {
      const response = await axios.post(
        "http://localhost:5000/compress",
        formData,
        {
          headers: {
            "Content-Type": "multipart/form-data",
          },
          onUploadProgress: (progressEvent) => {
            const { loaded, total } = progressEvent;
            const percentage = Math.round((loaded * 100) / total);
            setUploadProgress(percentage); // Update upload progress
          },
        }
      );
      console.log("API Response:", response.data);

      const { compressedFile, compressionRatio, outputFileSize } =
        response.data;
      const url = `http://localhost:5000/${compressedFile}`;

      setDownloadLink(url);
      setCompressionRatio(compressionRatio);
      setUploading(false);

      addRecord({
        fileName: file.name,
        compressionRatio: compressionRatio,
        inputFileSize: (file.size / (1024 * 1024)).toFixed(2),
        outputFileSize: outputFileSize,
      });
    } catch (error) {
      console.error("Error uploading file:", error);
    }
  };

  const handleDownload = () => {
    if (downloadLink) {
      const link = document.createElement("a");
      link.href = downloadLink;
      link.download = "";
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
    } else {
      alert("No file available to download.");
    }
  };
  return (
    <div className="fileToCompress">
      <div className="custom-file-upload">
        <input type="file" accept="audio/*" onChange={handleFileChange} />
      </div>

      <div className="uploadBtn">
        {uploading ? (
          <Progress precent={uploadProgress} />
        ) : (
          <button class="upl" onClick={handleUpload}>
            Convert
          </button>
        )}
      </div>

      <div className="compRat">
        {compressionRatio && (
          <div className="ratio">
            <p>Compression Ratio: {compressionRatio}%</p>
          </div>
        )}
      </div>
      {downloadLink && (
        <div className="downloadBtn">
          <button onClick={handleDownload}>Download Converted Audio</button>
        </div>
      )}
    </div>
  );
};

export default FileSelectComponent;
