import React, { useState } from "react";
import axios from "axios";

const TableComponent = () => {
  const [records, setRecords] = useState([]);
  const [showRecords, setShowRecords] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const fetchRecords = async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await axios.get(
        "http://localhost:5000/compression-records"
      );
      setRecords(response.data);
    } catch (error) {
      setError("Error fetching records");
    } finally {
      setLoading(false);
    }
  };

  const handleShowRecords = () => {
    if (!showRecords) {
      fetchRecords();
    }
    setShowRecords((prevShowRecords) => !prevShowRecords);
  };

  return (
    <div>
      <h1>Compression Records</h1>
      <button onClick={handleShowRecords}>Show Records</button>
      {loading && <p>Loading...</p>}
      {error && <p>{error}</p>}
      {showRecords && (
        <table>
          <thead>
            <tr>
              <th>File Name</th>
              <th>Compression Ratio (%)</th>
              <th>Compressed File</th>
              <th>Date</th>
            </tr>
          </thead>
          <tbody>
            {records.map((record, index) => (
              <tr key={index}>
                <td>{record.fileName}</td>
                <td>{record.compressionRatio}</td>
                {/* <td>
                  <a
                    href={record.compressedFile}
                    target="_blank"
                    rel="noopener noreferrer"
                  >
                    Download
                  </a>
                </td> */}
                <td>{new Date(record.date).toLocaleString()}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};
export default TableComponent;
