import React, { useState } from "react";
import axios from "axios";
import Compress from "./components/Compress";

function App() {
  const [file, setFile] = useState(null);
  const [downloadLink, setDownloadLink] = useState("");
  const [downloadLinkCon, setDownloadLinkCon] = useState("");
  const [compressionRatio, setCompressionRatio] = useState("");
  const [compressionRatioCon, setCompressionRatioCon] = useState("");
  const [uploading, setUploading] = useState(false);
  const [converting, setConverting] = useState(false);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleUpload = async () => {
    if (!file) {
      alert("Please select a file first.");
      return;
    }

    const formData = new FormData();
    formData.append("audio", file);

    setUploading(true);
    try {
      const response = await axios.post(
        "http://localhost:5000/compress",
        formData,
        {
          headers: {
            "Content-Type": "multipart/form-data",
          },
        }
      );

      const { compressedFile, compressionRatio } = response.data;
      const url = `http://localhost:5000/${compressedFile}`;

      setDownloadLink(url);
      setCompressionRatio(compressionRatio);
      setUploading(false);
    } catch (error) {
      console.error("Error uploading file:", error);
    }
  };

  const handleConvert = async () => {
    if (!file) {
      alert("Please select a file first.");
      return;
    }

    const formData = new FormData();
    formData.append("audio", file);

    setConverting(true);
    try {
      const response = await axios.post(
        "http://localhost:5000/convert",
        formData,
        {
          headers: {
            "Content-Type": "multipart/form-data",
          },
        }
      );

      const { convertedFile, compressionRatioCon } = response.data;
      const url = `http://localhost:5000/converted/${convertedFile}`;

      setDownloadLinkCon(url);
      setCompressionRatioCon(compressionRatioCon);
      setConverting(false);
    } catch (error) {
      console.error("Error converting file:", error);
      setConverting(false);
    }
  };
  const handleDownload = () => {
    const link = document.createElement("a");
    link.href = downloadLink;
    link.download = "convert_audio.m4a";
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };
  return (
    <div className="App">
      <h1>Audio Compression App</h1>

      {/* <h2>Compress</h2>
      <input type="file" accept="audio/*" onChange={handleFileChange} />
      <button onClick={handleUpload}>
        {uploading ? "Uploading..." : "Upload and Compress"}
      </button>
      {compressionRatio && (
        <div>
          <p>Compression Ratio: {compressionRatio}%</p>
        </div>
      )}

      {downloadLink && (
        <div>
          <button onClick={handleDownload}>Download Converted Audio</button>
        </div>
      )} */}
      <h2>Convert</h2>
      <div>
        <input type="file" accept="audio/*" onChange={handleFileChange} />
        <button onClick={handleConvert}>
          {converting ? "Uploading..." : "Upload and Convert"}
        </button>
      </div>
      {compressionRatioCon && (
        <div>
          <p>Compression Ratio: {compressionRatioCon}%</p>
        </div>
      )}
      {downloadLinkCon && (
        <div>
          <button onClick={handleDownload}>Download Converted Audio</button>
        </div>
      )}
    </div>
  );
}

export default App;
