import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router";
import axios from "axios";
import "./style.css";
import FileSelectComponent from "./FileSelectComponent";
import TableComponent from "./TableComponent.js";

const Compress = () => {
  const [records, setRecords] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const navigate = useNavigate();
  useEffect(() => {
    const savedState = sessionStorage.getItem("fileCompressState");
    if (savedState) {
      const {
        file: savedFile,
        downloadLink: savedLink,
        compressionRatio: savedRatio,
      } = JSON.parse(savedState);
      setFile(savedFile);
      setDownloadLink(savedLink);
      setCompressionRatio(savedRatio);
    }
  }, []);

  // Update sessionStorage whenever the state changes
  useEffect(() => {
    sessionStorage.setItem(
      "fileCompressState",
      JSON.stringify({
        file: file,
        downloadLink: downloadLink,
        compressionRatio: compressionRatio,
      })
    );
  }, [file, downloadLink, compressionRatio]);

  const fetchRecords = async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await axios.get(
        "http://localhost:5000/compression-records"
      );
      console.log("API Response:", response.data);
      setRecords(response.data);
    } catch (error) {
      setError("Error fetching records");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchRecords();
  }, []);
  const addRecord = (newRecord) => {
    setRecords((prevRecords) => [...prevRecords, newRecord]);
  };
  return (
    <div className="wrap">
      <div className="compresComp">
        <div className="title1">Convert</div>
        <div className="title2">Choose files to convert to MPEG-4 audio</div>
        <div className="wrapFileSelect">
          <FileSelectComponent addRecord={addRecord}></FileSelectComponent>
          <FileSelectComponent addRecord={addRecord}></FileSelectComponent>
          <FileSelectComponent addRecord={addRecord}></FileSelectComponent>
        </div>
      </div>
      <TableComponent records={records} loading={loading} error={error} />
    </div>
  );
};

export default Compress;
