import React, { useState } from "react";
import axios from "axios";
import "./style.css";

const Convert = () => {
  const [file, setFile] = useState(null);
  const [downloadLink, setDownloadLink] = useState("");
  const [downloadLinkCon, setDownloadLinkCon] = useState("");

  const [compressionRatioCon, setCompressionRatioCon] = useState("");

  const [converting, setConverting] = useState(false);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleConvert = async () => {
    if (!file) {
      alert("Please select a file first.");
      return;
    }

    const formData = new FormData();
    formData.append("audio", file);

    setConverting(true);
    try {
      const response = await axios.post(
        "http://localhost:5000/convert",
        formData,
        {
          headers: {
            "Content-Type": "multipart/form-data",
          },
        }
      );

      const { convertedFile, compressionRatioCon } = response.data;
      const url = `http://localhost:5000/converted/${convertedFile}`;

      setDownloadLinkCon(url);
      setCompressionRatioCon(compressionRatioCon);
      setConverting(false);
    } catch (error) {
      console.error("Error converting file:", error);
      setConverting(false);
    }
  };
  const handleDownload = () => {
    const link = document.createElement("a");
    link.href = downloadLink;
    link.download = "convert_audio.m4a";
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };
  return (
    <div className="wrap">
      <div className="title1">Convert</div>
      <div>
        <div className="custom-file-upload">
          <input type="file" accept="audio/*" onChange={handleFileChange} />
        </div>

        <button onClick={handleConvert}>
          {converting ? "Uploading..." : "Upload and Convert"}
        </button>
      </div>
      {compressionRatioCon && (
        <div>
          <p>Compression Ratio: {compressionRatioCon}%</p>
        </div>
      )}
      {downloadLinkCon && (
        <div>
          <button onClick={handleDownload}>Download Converted Audio</button>
        </div>
      )}
    </div>
  );
};

export default Convert;
