import React, { useEffect, useState } from "react";
import axios from "axios";
import "./style.css";
import FileSelectComponent from "./FileSelectComponent";

const Compress = () => {
  const [records, setRecords] = useState([]);
  //const [showRecords, setShowRecords] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const fetchRecords = async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await axios.get(
        "http://localhost:5000/compression-records"
      );
      setRecords(response.data);
    } catch (error) {
      setError("Error fetching records");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchRecords();
  }, []);
  const addRecord = (newRecord) => {
    setRecords((prevRecords) => [...prevRecords, newRecord]);
  };
  return (
    <div className="wrap">
      <div className="compresComp">
        <div className="title1">Convert</div>
        <div className="title2">Choose a files to convert to MPEG-4 audio</div>
        <FileSelectComponent addRecord={addRecord}></FileSelectComponent>
        <FileSelectComponent></FileSelectComponent>
        <FileSelectComponent></FileSelectComponent>
      </div>
      <TableComponent />
    </div>
  );
};

export default Compress;

