import axios from "axios";
import { useState } from "react";
import "./style.css";
import { Button, Upload } from "antd";

const Compress = () => {
  const [file, setFile] = useState(null);
  const [downloadLink, setDownloadLink] = useState("");
  const [compressionRatio, setCompressionRatio] = useState("");
  const [uploading, setUploading] = useState(false);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleUpload = async () => {
    if (!file) {
      alert("Please select a file first.");
      return;
    }

    const formData = new FormData();
    formData.append("audio", file);

    setUploading(true);
    try {
      const response = await axios.post(
        "http://localhost:5000/compress",
        formData,
        {
          headers: {
            "Content-Type": "multipart/form-data",
          },
        }
      );

      const { compressedFile, compressionRatio } = response.data;
      const url = `http://localhost:5000/${compressedFile}`;

      setDownloadLink(url);
      setCompressionRatio(compressionRatio);
      setUploading(false);
    } catch (error) {
      console.error("Error uploading file:", error);
    }
  };

  const handleDownload = () => {
    const link = document.createElement("a");
    link.href = downloadLink;
    link.download = "convert_audio.m4a";
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };
  return (
    <div className="wrap">
      <h2>Compress</h2>
      <input type="file" accept="audio/*" onChange={handleFileChange} />
      <Button type="primary">Primary Button</Button>
      <button onClick={handleUpload}>
        {uploading ? "Uploading..." : "Upload and Compress"}
      </button>
      {compressionRatio && (
        <div>
          <p>Compression Ratio: {compressionRatio}%</p>
        </div>
      )}

      {downloadLink && (
        <div>
          <button onClick={handleDownload}>Download Converted Audio</button>
        </div>
      )}
    </div>
  );
};

export default Compress;
