import React, { useEffect, useState } from "react";
import axios from "axios";
import "./style.css";
const TableComponent = () => {
  const [records, setRecords] = useState([]);
  const [showRecords, setShowRecords] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const fetchRecords = async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await axios.get(
        "http://localhost:5000/compression-records"
      );
      setRecords(response.data);
    } catch (error) {
      setError("Error fetching records");
    } finally {
      setLoading(false);
    }
  };
useEffect (()=>{
  fetchRecords()
})
  // const handleShowRecords = () => {
  //   if (!showRecords) {
  //     fetchRecords();
  //   }
  //   setShowRecords((prevShowRecords) => !prevShowRecords);
  // };

  return (
    <div className="tableComp">
      <div className="title3">Compression Records</div>{" "}
      {/* <button onClick={handleShowRecords}>Show Records</button> */}
      {loading && <p>Loading...</p>}
      {error && <p>{error}</p>}
      {/* {showRecords && (
        <div className="records">
          <table>
            <thead>
              <tr>
                <th>File Name</th>
                <th>Compression Ratio (%)</th>
                <th>Input File Size (MB)</th>
                <th>Compressed File Size (MB)</th>
              </tr>
            </thead>
            <tbody>
              {records.map((record, index) => (
                <tr key={index}>
                  <td>{record.fileName}</td>
                  <td>{record.compressionRatio}</td>
                  <td>{record.inputFileSize}</td>
                  <td>{record.outputFileSize}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )} */}
      <div className="records">
        <table>
          <thead>
            <tr>
              <th>File Name</th>
              <th>Compression Ratio (%)</th>
              <th>Input File Size (MB)</th>
              <th>Compressed File Size (MB)</th>
            </tr>
          </thead>
          <tbody>
            {records.map((record, index) => (
              <tr key={index}>
                <td>{record.fileName}</td>
                <td>{record.compressionRatio}</td>
                <td>{record.inputFileSize}</td>
                <td>{record.outputFileSize}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};
export default TableComponent;
