import "./style.css";
import FileSelectComponent from "./FileSelectComponent";

const Compress = () => {
  return (
    <div className="wrap">
      <div className="compresComp">
        <div className="title1">Convert</div>
        <div className="title2">Choose a files to convert to MPEG-4 audio</div>
        <FileSelectComponent></FileSelectComponent>
        <FileSelectComponent></FileSelectComponent>
        <FileSelectComponent></FileSelectComponent>
      </div>
      <TableComponent />
    </div>
  );
};

export default Compress;

