import axios from "axios";
import { useState, useEffect } from "react";
import "./style.css";
import { Spin } from "antd";

const FileSelectComponent = ({ addRecord }) => {
  const [file, setFile] = useState(null);
  const [downloadLink, setDownloadLink] = useState("");
  const [compressionRatio, setCompressionRatio] = useState("");
  const [uploading, setUploading] = useState(false);

  useEffect(() => {
    const savedState = JSON.parse(sessionStorage.getItem("fileCompressState"));
    if (savedState) {
      setFile(savedState.file);
      setDownloadLink(savedState.downloadLink);
      setCompressionRatio(savedState.compressionRatio);
    }
  }, []);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleUpload = async () => {
    if (!file) {
      alert("Please select a file first.");
      return;
    }

    const formData = new FormData();
    formData.append("audio", file);

    setUploading(true);

    try {
      const response = await axios.post(
        "http://localhost:5000/compress",
        formData
      );

      const { compressedFile, compressionRatio, outputFileSize } =
        response.data;
      const url = `http://localhost:5000/${compressedFile}`;

      setDownloadLink(url);
      setCompressionRatio(compressionRatio);
      setUploading(false);

      addRecord({
        fileName: file.name,
        compressionRatio: compressionRatio,
        inputFileSize: (file.size / (1024 * 1024)).toFixed(2),
        outputFileSize: outputFileSize,
      });
    } catch (error) {
      console.error("Error uploading file:", error);
      setUploading(false);
    }
  };

  const handleDownload = () => {
    if (downloadLink) {
      // Save the current state in sessionStorage
      sessionStorage.setItem(
        "fileCompressState",
        JSON.stringify({
          file: file,
          downloadLink: downloadLink,
          compressionRatio: compressionRatio,
        })
      );

      const link = document.createElement("a");
      link.href = downloadLink;
      link.download = "";
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
    } else {
      alert("No file available to download.");
    }

    // if (downloadLink) {
    //   const link = document.createElement("a");
    //   link.href = downloadLink;
    //   link.download = "";
    //   document.body.appendChild(link);
    //   link.click();
    //   document.body.removeChild(link);
    // } else {
    //   alert("No file available to download.");
    // }
  };

  return (
    <div className="fileToCompress">
      <div className="custom-file-upload">
        <input type="file" accept="audio/*" onChange={handleFileChange} />
      </div>
      <div className="uploadBtn">
        {uploading ? (
          <Spin tip="Loading" size="large"></Spin>
        ) : (
          <button class="upl" onClick={handleUpload}>
            Convert
          </button>
        )}
      </div>
      <div className="rationDown">
        {compressionRatio && (
          <div className="ratio">
            <p>Compression Ratio: {compressionRatio}%</p>
          </div>
        )}

        {downloadLink && (
          <div className="downloadBtn">
            <button onClick={handleDownload}>Download</button>
          </div>
        )}
      </div>
    </div>
  );
};

export default FileSelectComponent;
