import React, { useEffect, useState } from "react";
import axios from "axios";
import "./style.css";
import FileSelectComponent from "./FileSelectComponent";
import TableComponent from "./TableComponent.js";

const Compress = () => {
  const [records, setRecords] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    const savedRecords = JSON.parse(
      sessionStorage.getItem("compressionRecords")
    );
    if (savedRecords) {
      // Assuming there's a way to set records in the parent component
      // For example, you could have a setRecords function passed as a prop
      setRecords(savedRecords);
    }
  }, []);

  // Save records to sessionStorage whenever they change
  useEffect(() => {
    sessionStorage.setItem("compressionRecords", JSON.stringify(records));
  }, [records]);

  const fetchRecords = async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await axios.get(
        "http://localhost:5000/compression-records"
      );
      console.log("API Response:", response.data);
      setRecords(response.data);
    } catch (error) {
      setError("Error fetching records");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchRecords();
  }, []);
  const addRecord = (newRecord) => {
    setRecords((prevRecords) => [...prevRecords, newRecord]);
  };

  return (
    <div className="wrap">
      <div className="compresComp">
        <div className="title1">Convert</div>
        <div className="title2">Choose files to convert to MPEG-4 audio</div>
        <div className="wrapFileSelect">
          <FileSelectComponent addRecord={addRecord}></FileSelectComponent>
          <FileSelectComponent addRecord={addRecord}></FileSelectComponent>
          <FileSelectComponent addRecord={addRecord}></FileSelectComponent>
        </div>
      </div>
      <TableComponent records={records} loading={loading} error={error} />
    </div>
  );
};

export default Compress;
